mapper.select:
SELECT P.BOARDNO,T.TASKNO,TASKTITLE,TASKSTATE,TASKPROIORITY,TASKCONTENT,TASKID,P.PROJECTNO,BOARDTYPE,PINPOST, TO_CHAR(TO_DATE(TASKSTARTDATE), 'yyyy/mm/dd') AS TASKSTARTDATE,TO_CHAR(TO_DATE(TASKENDDATE), 'yyyy/mm/dd') AS TASKENDDATE FROM TASK T JOIN PROJECTBOARD P ON T.BOARDNO=P.BOARDNO JOIN TASKMANAGER TM ON T.TASKNO=TM.TASKNO
PROJECTNO=#{projectNo}
AND TASKID=#{userId}
AND TASKID=#{userId} OR TASKMANAGERID=#{userId}
AND TASKSTATE=#{status}
AND TASKPROIORITY=#{priority}
AND TO_CHAR(TASKSTARTDATE,'yyyy/mm/dd') =TO_CHAR(SYSDATE,'yyyy/mm/dd')
AND TO_CHAR(TASKSTARTDATE,'yyyy/mm/dd') IN (SELECT TRUNC(SYSDATE, 'DAY') + LEVEL - 1 FROM DUAL CONNECT BY LEVEL <=7)
AND TO_CHAR(TASKSTARTDATE,'yyyy/mm/dd') BETWEEN TO_CHAR(ADD_MONTHS(SYSDATE,-1),'yyyy/mm/dd') AND TO_CHAR(ADD_MONTHS(SYSDATE,+1),'yyyy/mm/dd')
AND TO_CHAR(TASKENDDATE,'yyyy/mm/dd') < TO_CHAR(SYSDATE,'yyyy/mm/dd')
AND TO_CHAR(TASKENDDATE,'yyyy/mm/dd') = TO_CHAR(SYSDATE,'yyyy/mm/dd')
AND TO_CHAR(TASKENDDATE,'yyyy/mm/dd') IN (SELECT TRUNC(SYSDATE, 'DAY') + LEVEL - 1 FROM DUAL CONNECT BY LEVEL <=7)
AND TO_CHAR(TASKENDDATE,'yyyy/mm/dd') BETWEEN TO_CHAR(ADD_MONTHS(SYSDATE,-1),'yyyy/mm/dd') AND TO_CHAR(ADD_MONTHS(SYSDATE,+1),'yyyy/mm/dd')
